










update o02agpe0.TB_MTOINF_VISTAS_CAMPOS vc
set vc.nombre = 'NUM_ANHOS_DESDE_DESCORCHE', vc.nombre_real_campo = 'NUM_ANHOS_DESDE_DESCORCHE' 
where vc.idvista = (select v.id from o02agpe0.TB_MTOINF_VISTAS v where v.NOMBRE_REAL = 'TB_INF_POLIZAS_PARCELAS')
and vc.nombre like 'NUM_A%DESCORCHE';

update o02agpe0.TB_MTOINF_VISTAS_CAMPOS vc
set vc.nombre = 'NUM_ANHOS_DESDE_PODA', vc.nombre_real_campo = 'NUM_ANHOS_DESDE_PODA' 
where vc.idvista = (select v.id from o02agpe0.TB_MTOINF_VISTAS v where v.NOMBRE_REAL = 'TB_INF_POLIZAS_PARCELAS')
and vc.nombre like 'NUM_A%PODA';

