	@Override
	protected String generaInsert(Object reg, String dateFormat) {
		EleccionesGarantizado registro = (EleccionesGarantizado)reg;
		String sql = "";
		sql += registro.getId().getLineaseguroid() + ";";
		sql += StringUtils.nullToString(registro.getModulo().getId().getCodmodulo().trim())+ ";";
		sql += StringUtils.nullToString(registro.getTipoCapital().getCodtipocapital()) + ";";
		sql += StringUtils.nullToString(registro.getCodConceptoPpal()) + ";";
		sql += StringUtils.nullToString(registro.getCodRiesgoCbto()) + ";";
		sql += StringUtils.nullToString(registro.getValorGarantizado()) + ";";
		sql += StringUtils.nullToString(registro.getNumMinRega()) + ";";
		sql += StringUtils.nullToString(registro.getNumMinSubexplota()) + ";";
		sql += registro.getId().getId() + ";";
		return sql;
	}

	@Override
	protected Object generaRegistro(Object actual, String tag, XMLStreamReader parser, int id, Long lineaseguroid) {
		EleccionesGarantizado registro;
		if (actual == null){
			registro = new EleccionesGarantizado();
		}
		else{
			registro = (EleccionesGarantizado) actual;
		}

		EleccionesGarantizadoId idActual = new EleccionesGarantizadoId();

		if (this.getTagPrincipal().equals(tag)){
			
			idActual.setLineaseguroid(lineaseguroid);
			idActual.setId(new Long(id));
			
			if (!StringUtils.nullToString(parser.getAttributeValue(null, "mod")).equals("")){
				registro.getModulo().getId().setCodmodulo(StringUtils.nullToString(parser.getAttributeValue(null, "mod")).trim());
			}
			
			if (!StringUtils.nullToString(parser.getAttributeValue(null, "tipCptal")).equals("")){
				registro.getTipoCapital().setCodtipocapital(new BigDecimal(StringUtils.nullToString(parser.getAttributeValue(null, "tipCptal"))));
			}
			if (!StringUtils.nullToString(parser.getAttributeValue(null, "garant")).equals("")){
				registro.setValorGarantizado(new Long(StringUtils.nullToString(parser.getAttributeValue(null, "garant"))));
			}

			if (!StringUtils.nullToString(parser.getAttributeValue(null, "cPMod")).equals("")){
				registro.setCodConceptoPpal(new Long(StringUtils.nullToString(parser.getAttributeValue(null, "cPMod"))));
			}
			
			if (!StringUtils.nullToString(parser.getAttributeValue(null, "codRCub")).equals("")){
				registro.setCodRiesgoCbto(new Long(StringUtils.nullToString(parser.getAttributeValue(null, "codRCub"))));
			}

			if (!StringUtils.nullToString(parser.getAttributeValue(null, "numMinREGAs")).equals("")){
				registro.setNumMinRega(new Long(StringUtils.nullToString(parser.getAttributeValue(null, "numMinREGAs"))));
			}
			
			if (!StringUtils.nullToString(parser.getAttributeValue(null, "numMinSubexp")).equals("")){
				registro.setNumMinSubexplota(new Long(StringUtils.nullToString(parser.getAttributeValue(null, "numMinSubexp"))));
			}
			
			registro.setId(idActual);

		}
		return registro;
	}
