	@Override
	protected String generaInsert(Object reg, String dateFormat) {
		GruposIncompatibilidad registro = (GruposIncompatibilidad)reg;
		String sql = "";
		sql += registro.getId().getLineaseguroid() + ";";
		sql += StringUtils.nullToString(registro.getId().getCodmodulo()) + ";";
		sql += StringUtils.nullToString(registro.getId().getCodGrupoEspAmb()) + ";";
		sql += StringUtils.nullToString(registro.getDescripcion()) + ";";
		
		return sql;
	}

	@Override
	protected Object generaRegistro(Object actual, String tag, XMLStreamReader parser, int id, Long lineaseguroid) {
		GruposIncompatibilidad registro;
		if (actual == null){
			registro = new GruposIncompatibilidad();
		}
		else{
			registro = (GruposIncompatibilidad) actual;
		}

		GruposIncompatibilidadId idActual = new GruposIncompatibilidadId();
		
		if (this.getTagPrincipal().equals(tag)){
		
			idActual.setLineaseguroid(lineaseguroid);
			
			if (!StringUtils.nullToString(parser.getAttributeValue(null, "mod")).equals("")){
				idActual.setCodmodulo(StringUtils.nullToString(parser.getAttributeValue(null, "mod")).trim());
			}
			
			if (!StringUtils.nullToString(parser.getAttributeValue(null, "grupEspAmb")).equals("")){
				idActual.setCodGrupoEspAmb(new Long(StringUtils.nullToString(parser.getAttributeValue(null, "grupEspAmb"))));
			}
			
			if (!StringUtils.nullToString(parser.getAttributeValue(null, "desc")).equals("")){
				registro.setDescripcion(StringUtils.nullToString(parser.getAttributeValue(null, "desc")));
			}

			registro.setId(idActual);

		}

		return registro;
	}
